svg.script:
let lastFrame = 0;

const update = (ms, delta) => {
  const rect = document.querySelector("rect");
  rect.style.transform = `rotate(${(ms / 100) * delta}deg)`;
};

const animate = (ms = 0) => {
  const delta = ms - lastFrame;

  if(document.visibilityState === "visible") {
    update(ms, delta);
  }

  lastFrame = ms;
  requestAnimationFrame(animate);
};
globalThis.onload = animate;
